package simple;

public class LoopingStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//for
  for(int i =0; i <= 20 ; i++)
  {
 System.out.println(i);
	}
  // while PRE DICIDED
  int i = 0;
  while(i<20) {
	  System.out.println(": " +i);
	  i++;
  }
  int j =0;
  int k=10;
  while (j<k) {
	  System.out.println("j :"+j+"k "+k);
	  j++;
	  k--;
  
  }
	}}

